init.item:
var amount = 0;
amount = YS_TDYL_VALUE + YS_TDZL_VALUE;
return amount;

if(YS_TDZL_VALUE == 0) {
	return 0;
}
var Q = YS_TDZL_VALUE/UNIT;
var premium_tdzl = 0;
if(AGE ge 0 and AGE le 4) {
	premium_tdzl = Q*0.20;
}
if(AGE ge 5 and AGE le 9){
	premium_tdzl = Q*0.10;
}
if(AGE ge 10 and AGE le 14){
	premium_tdzl = Q*0.10;
}
if(AGE ge 15 and AGE le 19){
	premium_tdzl = Q*0.20;
}
if(AGE ge 20 and AGE le 24){
	premium_tdzl = Q*0.20;
}
if(AGE ge 25 and AGE le 29){
	premium_tdzl = Q*0.40;
}
if(AGE ge 30 and AGE le 34){
	premium_tdzl = Q*0.80;
}
if(AGE ge 35 and AGE le 39){
	premium_tdzl = Q*1.30;
}
if(AGE ge 40 and AGE le 44){
	premium_tdzl = Q*2.20;
}
if(AGE ge 45 and AGE le 49){
	premium_tdzl = Q*3.30;
}
if(AGE ge 50 and AGE le 54){
	premium_tdzl = Q*4.80;
}
if(AGE ge 55 and AGE le 59){
	premium_tdzl = Q*6.80;
}
if(AGE ge 60 and AGE le 64){
	premium_tdzl = Q*9.20;
}
return round(premium_tdzl*0.9,2);

if(YS_TDYL_VALUE == 0) {
	return 0;
}
var premium_tdyl = 0;
if(AGE ge 0 and AGE le 4) {
	premium_tdyl = 90.00;
}
if(AGE ge 5 and AGE le 9){
	premium_tdyl = 90.00;
}
if(AGE ge 10 and AGE le 14){
	premium_tdyl = 90.00;
}
if(AGE ge 15 and AGE le 19){
	premium_tdyl = 100.00;
}
if(AGE ge 20 and AGE le 24){
	premium_tdyl = 100.00;
}
if(AGE ge 25 and AGE le 29){
	premium_tdyl = 109.00;
}
if(AGE ge 30 and AGE le 34){
	premium_tdyl = 109.00;
}
if(AGE ge 35 and AGE le 39){
	premium_tdyl = 146.00;
}
if(AGE ge 40 and AGE le 44){
	premium_tdyl = 146.00;
}
if(AGE ge 45 and AGE le 49){
	premium_tdyl = 237.00;
}
if(AGE ge 50 and AGE le 54){
	premium_tdyl = 237.00;
}
if(AGE ge 55 and AGE le 59){
	premium_tdyl = 273.00;
}
if(AGE ge 60 and AGE le 64){
	premium_tdyl = 273.00;
}
return round(premium_tdyl*0.9,2);
