init.item:
var ret=0;
if(PRODUCT_SORT_CODE =='sort_1'){
if(AGE le 40){
ret= 130;
}else if(AGE le 70){
ret= 115;
}else if(AGE ge 71){
ret=101;
}
}else if(PRODUCT_SORT_CODE =='sort_2'){
if(AGE le 40){
ret= 30;
}else if(AGE le 70){
ret= 15;
}else if(AGE ge 71){
ret=1;
}
}
return ret;

var ret = 0;
if(CURRENCY_CODE != null and P1 != null) {
